        // TODO: Tab to indent
        const editing = this.editing.get();
        if (event.key === 'i' && event.ctrlKey) {
            this.toggleEditMode();
        } else if (!editing) {
            this.parent.handleCellKeyDown(event, this.index.get());
        }
    }
}

